      "options_pmp": {
        "OPTION_A": "A lack of physiological needs fulfillment",
        "OPTION_B": "A high need for affiliation",
        "OPTION_C": "A high need for achievement",
        "OPTION_D": "A low need for power"
      },
      "is_attempted": false,
      "is_valid": false,
      "selected_option": "",
      "question_type": "Option",
